import * as development from './config/development'
import * as staging from './config/staging'
import * as production from './config/production'

let config = null

switch (process.env.SCOUT_SDK_ENV) {
  case 'development':
    config = development
    break
  case 'staging':
    config = staging
    break
  default:
  case 'production':
    config = production
    break
}

export default config
