import { graphQuery } from './comms'

export async function get(id: string) {
  return await graphQuery(
    `
    query Persona($id: String) {
      persona(id: $id) {
        id
        handle
        pictureUrl
        countryCode
        region
        isVerified
        platform {
          slug
          name
        }
      }
    }
    `, { id }, (data: any) => data.persona
  )
}
