import { graphQuery } from './comms'

export async function getData(title: string, method: string, parameters: any[]) {
  return await graphQuery(
    `
    query Raw($title: String, $method: String, $parameters: [Dynamic]) {
      raw(title: $title, method: $method, parameters: $parameters)
    }
    `,
    {
      title,
      method,
      parameters
    },
    (data: any) => data.raw.data
  )
}
